CREATE TABLE Rule(_id INTEGER PRIMARY KEY NOT NULL, name TEXT NOT NULL, enable INTEGER NOT NULL, vibrate INTEGER NOT NULL);
CREATE TABLE CalendarRule(_id INTEGER PRIMARY KEY NOT NULL, matchAll INTEGER NOT NULL, matchTitle INTEGER NOT NULL, matchDesc INTEGER NOT NULL, inverseMatch INTEGER NOT NULL NOT NULL, FOREIGN KEY (_id) REFERENCES Rule (_id) ON UPDATE CASCADE ON DELETE CASCADE);
CREATE TABLE CalendarRuleCalendar(_id INTEGER PRIMARY KEY NOT NULL, rule INTEGER NOT NULL, calendarID INTEGER NOT NULL, FOREIGN KEY (rule) REFERENCES Rule (_id) ON UPDATE CASCADE ON DELETE CASCADE);
CREATE TABLE CalendarRuleKeyword(_id INTEGER PRIMARY KEY NOT NULL, rule INTEGER NOT NULL, word TEXT NOT NULL, FOREIGN KEY (rule) REFERENCES Rule (_id) ON UPDATE CASCADE ON DELETE CASCADE);
CREATE TABLE ScheduleRule(_id INTEGER PRIMARY KEY NOT NULL, begin INTEGER NOT NULL, end INTEGER NOT NULL, sunday INTEGER NOT NULL, monday INTEGER NOT NULL, tuesday INTEGER NOT NULL, wednesday INTEGER NOT NULL, thursday INTEGER NOT NULL, friday INTEGER NOT NULL, saturday INTEGER NOT NULL, FOREIGN KEY (_id) REFERENCES Rule (_id) ON UPDATE CASCADE ON DELETE CASCADE);
